    public static function datediffInWeeks($date1, $date2)
    {
        $cohortStartDate = new DateTime($date1);
        $cohortStartDate = $cohortStartDate->sub(new DateInterval('P1D'));  // Minus one day to reach sunday
        $today = new DateTime($date2);
        return floor($cohortStartDate->diff($today)->days / 7) + 1;
    }


    /*
     *
     * Shortcode Output
    */
    public static function shortcode($c){
        $user = Auth::user();
        $content = $c; // Assign string to new var
        preg_match('/"([^"]+)"/', $c, $assignmentID); // find Course ID in original string
        if(array_key_exists(0, $assignmentID)){
            //$aID = substr($assignmentID[0], 1, 2); // Get id from "id" 
            $aID = $assignmentID[1];

            if(!is_numeric($aID)){
                return $content;
            }

            $entry = $user->assignments()->where('assignment_id', $aID)->first();
            if($entry){
                $content = preg_replace('#\[.*?\]#s', '<strong>'.$entry->pivot->entry.'</strong>', $content);
                return $content;
            }

        } else {
            return $content; // No Shortcode
        }
    }



    public static function convertDBValue($key, $value) {

        if (!$value || !$key) {
            return;
        };

        if (isset(self::DB_MAP_VALUES[$key])) {

            return ($value);

        }

        return $value;
    }



    public static function convertDBKey($key) {

        if (isset(self::DB_MAP_KEYS[$key])) {

            return (self::DB_MAP_KEYS[$key]);

        }

        return $key;
    }


